import React, { useState } from 'react';
import EduItem from './EduItem'; 
import { EasyData, MiddleData, HardData } from './Edu';

const Book_button = () => {
  const [category, setCategory] = useState('Edu');

  return (
    <div>
      <div className="title_wrap">
        <div className="container">
          <div className="theme_title">
            <div className="choice">
              <button
                className={category === 'Easy' ? 'active' : ''}
                onClick={() => setCategory('Easy')}
              >
                로맨스
              </button>
              <button
                className={category === 'Middle' ? 'active' : ''}
                onClick={() => setCategory('Middle')}
              >
                청소년
              </button>
              <button
                className={category === 'Hard' ? 'active' : ''}
                onClick={() => setCategory('Hard')}
              >
                청소년
              </button>
            </div>
          </div>
          <div className="box_wrap">
            {category === 'Easy' && RomanceData.map((item, index) => (
              <EduItem
                key={index}
                title={item.title}
                location={item.location}
                moreinfo={item.moreinfo}
                imgSrc={item.imgSrc}
                imgAlt={item.imgAlt}
              />
            ))}
            {category === 'Middle' && TeenagerData.map((item, index) => (
              <EduItem
                key={index}
                title={item.title}
                location={item.location}
                moreinfo={item.moreinfo}
                imgSrc={item.imgSrc}
                imgAlt={item.imgAlt}
              />
            ))}
            {category === 'Hard' && RomanceData.map((item, index) => (
              <EduItem
                key={index}
                title={item.title}
                location={item.location}
                moreinfo={item.moreinfo}
                imgSrc={item.imgSrc}
                imgAlt={item.imgAlt}
              />
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Book_button;
