import React, { useState } from 'react';
import MedicineItem from './MedicineItem';
import { VitaminAData, VitaminBData } from '../Medicine'; 

const Medi = () => {
  const [category, setCategory] = useState('theme');
  const data = category === 'theme' ? VitaminAData : VitaminBData;

  return (
    <div className="medicine-page">
      <div className="title_wrap">
        <div className="container">
          <div className="theme_title">
            <div className="choice">
              <button
                className={category === 'theme' ? 'active' : ''}
                onClick={() => setCategory('theme')}
              >
                멀티비타민
              </button>
              <button
                className={category === 'general' ? 'active' : ''}
                onClick={() => setCategory('general')}
              >
                오메가3
              </button>
            </div>
          </div>
          <div className="box_wrap">
            {data.map((item, index) => (
              <MedicineItem
                key={index}
                title={item.title}
                location={item.location}
                moreinfo={item.moreinfo}
                imgSrc={item.imgSrc}
                imgAlt={item.imgAlt}
              />
            ))}
          </div>
        </div>
      </div>
    </div>
  );
}

export default Medi;
